from random import choice
import config
import telebot
bot = telebot.TeleBot(token=config.BOT_token)

jokes = [
    "Почему программисты не любят природу? Потому что в природе слишком много ошибок.",
    "Как программист находит свою жену? Находит в Google!",
    "Зачем программист ставит будильник? Чтобы не проснуться в 'праздник'!",
    "Почему коты всегда наступают на клавиши? Они ищут 'мяу' на клавиатуре.",
    "Какой язык программирования самый оптимистичный? Соло!"
]


# Handle '/start' and '/help'
@bot.message_handler(commands=['help', 'start'])
def send_welcome(message):
    bot.reply_to(message, """\
Прувет,я бот сосисочник,очень люблу сасиски
""")

@bot.message_handler(commands=['кола'])
def send_welcome(message):
    bot.reply_to(message, """\
    Перейди по это ссылке - data:image/jpeg;base64,\
    """)

@bot.message_handler(commands=['watermelon'])
def send_welcome(message):
    bot.reply_to(message, """\
     арбуз,арбуз перни
            """)
@bot.message_handler(commands=['arbuz'])
def arbuz_handler(message):
    arbuz = choice(["Арбуз,арбуз перни", "Арбуз,арбуз привет"])
    bot.reply_to(message, arbuz)

@bot.message_handler(commands=['joke'])
def joke_handler(message):
    joke = choice(["Почему программисты не любят природу? Потому что в природе слишком много ошибок.",
    "Как программист находит свою жену? Находит в Google!"])
    bot.reply_to(message, joke)

@bot.message_handler(commands=['info'])
def on_info(message):
    bot.reply_to(message,"""\
    бебебе
                        """)



# Handle all other messages with content_type 'text' (content_types defaults to ['text'])
@bot.message_handler(func=lambda message: True)
def echo_message(message):
    bot.reply_to(message, message.text)


bot.infinity_polling()